import styled from "styled-components";

export const Text = styled.div`
    p{
        text-align: left;
        margin-right: 50%;
    }
    color: #000;
    top: 20px;
    margin-left: 50px;
    margin-right: 200px;

`
export const ImageAwards = styled.div`
    img{margin-top: 5px;
        margin-left: 50%;
        max-width: 200px;
        max-height: 200px;
        position: absolute;
    }
    
`
